import { useEffect, useState } from 'react';
import './App.css';
import HangmanDrawing from './HangmanDrawing';
import HangmanWord from './HangmanWord';
import Keyboard from './Keyboard';

function App() {
  const words = ["hangman", "relax", "sun", "glasses", "sport", "tension", "normal", "move", "share", "friend"]
  const [guessedLetters, setGuessedLetters] = useState([]) 


  useEffect(()=>{

    const handleKeyPress =(e)=>{
      if(!e.key.test(/[a-z]/)) return
      e.preventDefault()
      console.log(guessedLetters, "running1")
      setGuessedLetters([...guessedLetters, e.key])
    }

    document.addEventListener('keypress', handleKeyPress)

    return document.removeEventListener('keypress', handleKeyPress)
  }, [guessedLetters])

  return (
    <div className="App">
     hello world...
     <HangmanDrawing/>
     <HangmanWord words={words} guessedLetters={guessedLetters} setGuessedLetters={setGuessedLetters}/>
     <Keyboard/>
    </div>
  );
}

export default App;
