import { useEffect, useState } from 'react';
import './App.css';
import HangmanDrawing from './HangmanDrawing';
import HangmanWord from './HangmanWord';
import Keyboard from './Keyboard';

function App() {
  console.log("running1")
  const words = ["hangman", "relax", "sun", "glasses", "sport", "tension", "normal", "move", "share", "friend", "couple", "audience", "band", "enough", "woman"]
// eslint-disable-next-line
  const [word, setWord] = useState(words[Math.floor(Math.random()* 15)])
  const [guessedLetters, setGuessedLetters] = useState([])
  const [pressedKey, setPressedKey] = useState("")

  const incorrectLetters = guessedLetters.filter(letter=> !word.includes(letter))
  const isLoser = incorrectLetters.length>= 6
  const isWinner = word.split("").every((letter)=> guessedLetters.includes(letter))


  console.log(word, "running2")

  useEffect(()=>{
    console.log(guessedLetters, pressedKey, "running3c")
    if(!pressedKey) return
    console.log(guessedLetters,pressedKey, "running3d")
    setGuessedLetters(prev=>[...prev, pressedKey])
    console.log(guessedLetters, "running3e")

  },[pressedKey])



  useEffect(()=>{


    const handleKeyPress = (e)=>{
      console.log(guessedLetters, "running3")
      if(!/[a-z]/.test(e.key)) return
      console.log(guessedLetters, "running3b")
      e.preventDefault()
      setPressedKey(e.key)
      console.log(guessedLetters, pressedKey, "running3bb")
    }

    console.log(guessedLetters, "running6")

    document.addEventListener('keypress', handleKeyPress)

    console.log(guessedLetters, "running7")

    return ()=> {
      console.log(guessedLetters, "running8")

      document.removeEventListener('keypress', handleKeyPress)

      console.log(guessedLetters, "running9")}
    // eslint-disable-next-line
}, [])

  return (
    <div className="App">
      {console.log(guessedLetters, "running10")}
     {isWinner && "You Win!!.. Reload to play again"}
     {isLoser && "Nice Try.. Reload to play again"}
     <HangmanDrawing incorrectLetters={incorrectLetters}/>
     <HangmanWord word={word} guessedLetters={guessedLetters} setGuessedLetters={setGuessedLetters} isLoser={isLoser} isWinner={isWinner} />
     <Keyboard/>
    </div>
  );
}

export default App;
