import { useEffect, useState } from 'react';
import './App.css';
import HangmanDrawing from './HangmanDrawing';
import HangmanWord from './HangmanWord';
import Keyboard from './Keyboard';

function App() {
  const words = ["hangman", "relax", "sun", "glasses", "sport", "tension", "normal", "move", "share", "friend"]
  const [guessedLetters, setGuessedLetters] = useState(['a','e','i','o','u']) 

  const handleKeyPress =(e)=>{
    setGuessedLetters([...guessedLetters, e.key])
  }

  useEffect(()=>{
    const keyEvent = document.addEventListener(onkeydown, handleKeyPress)

    return removeEventListener(keyEvent)
  })

  return (
    <div className="App">
     hello world...
     <HangmanDrawing/>
     <HangmanWord words={words} guessedLetters={guessedLetters} setGuessedLetters={setGuessedLetters}/>
     <Keyboard/>
    </div>
  );
}

export default App;
