import { useEffect, useState } from 'react';
import './App.css';
import HangmanDrawing from './HangmanDrawing';
import HangmanWord from './HangmanWord';
import Keyboard from './Keyboard';

function App() {
  console.log("running1")
  const words = ["hangman", "relax", "sun", "glasses", "sport", "tension", "normal", "move", "share", "friend"]
  const [word, setWord] = useState(words[Math.floor(Math.random()* 10)])
  const [guessedLetters, setGuessedLetters] = useState([]) 
  console.log(guessedLetters, "running2")

  const addToGuessedLetters =(letter)=>{
    console.log(guessedLetters, "running4")
    setGuessedLetters(prev=>[...prev, letter])
    console.log(guessedLetters, "running5")
  }


  useEffect(()=>{

    const handleKeyPress =(e)=>{
      console.log(guessedLetters, "running3")
      e.preventDefault()
      if(!/[a-z]/.test(e.key)) return
      const letter = e.key
      addToGuessedLetters(letter)
    }

    console.log(guessedLetters, "running6")


    document.addEventListener('keypress', handleKeyPress)
    console.log(guessedLetters, "running7")

    return ()=> {
      console.log(guessedLetters, "running8")

      document.removeEventListener('keypress', handleKeyPress)
      console.log(guessedLetters, "running9")}

  }, [])

  return (
    <div className="App">
      {console.log(guessedLetters, "running10")}
     hello world...
     <HangmanDrawing/>
     <HangmanWord word={word} guessedLetters={guessedLetters} setGuessedLetters={setGuessedLetters}/>
     <Keyboard/>
    </div>
  );
}

export default App;
