import React from "react";
import NoteForm from './NoteForm';
import { useParams } from 'react-router-dom';

const Editnote = ({onSubmit, onAddTag, tags, notes})=>{
    const {id} = useParams()
    console.log(id, "let me see id")
    const noteToEdit = notes.find(n=>n.id===id)
    const tagsToEdit = tags.map(tag=> noteToEdit.tagIds.includes(tag.id))

    return(
        <div>
            <h1>
            Edit note
            </h1>
            <NoteForm onSubmit={onSubmit} onAddTag={onAddTag} tags={tagsToEdit} noteToEdit={noteToEdit}/>
        </div>
    )
}

export default Editnote;
