import React, { useState } from "react";
import CreatableReactSelect from "react-select/creatable"

const NoteForm = ()=>{
    const [selectedTags, setSelectedTags] = useState([])
    const handleSubmit = (data)=>{
        return({
            title:data.tile,
            tag:data.tag,
            markdown:data.markdown
        })
    }
    return(
        <div className="container">
            <div className="container_title_tags">
                <form action={(data)=>handleSubmit(data)}>
                    <label htmlFor="title">Title</label>
                    <input type="text" id="title" name="title" value={"title"}/>
                    <label htmlFor="tags">Tags</label>
                    <CreatableReactSelect
                        isMulti 
                        value={()=>{selectedTags.map((tag)=>{return {label:tag.label, value:tag.id}})}}
                        onChange={tag=>{console.log(tag, "tag"); setSelectedTags(tag.map(()=>{return {label:tag.label, id:tag.value}}))}} />
                    <div className="container_body">
                        <input type="textarea" />
                     </div>
                </form>
            </div>
        </div>
    )
}

export default NoteForm;
