import React from "react";
import NoteForm from './NoteForm';
import { useParams } from 'react-router-dom';

const Editnote = ({onCreateNote, onAddTag, tags, notes})=>{
    const id = useParams()
    const noteToEdit = notes.find(n=>n.id===id)
    return(
        <div>
            <h1>
            Edit note
            </h1>
            <NoteForm onCreateNote={onCreateNote} onAddTag={onAddTag} tags={tags} noteToEdit={noteToEdit}/>
        </div>
    )
}

export default Editnote;
