import React, { useRef, useState } from "react";
import CreatableReactSelect from "react-select/creatable"

const NoteForm = ()=>{
    const [selectedTags, setSelectedTags] = useState([])
    const [datat, setDatat] = useState(null)
    const titleRef = useRef(null)
    const bodyRef = useRef(null)

    // let datat;
    // const handleSubmit = (data)=>{
    //     console.log(data, "data")
    //     datat = data
        // return({
        //     title:data.title,
        //     tag:data.tag,
        //     markdown:data.markdown
        // })
    // }
    return(
        <div className="container">
            <div className="container_title_tags">
                <form onSubmit={(data)=> setDatat(data)}>
            {console.log(datat, "datat")}

                    <label htmlFor="title">Title</label>
                    <input ref={titleRef} type="text" id="title" name="title" />
                    <label htmlFor="tags">Tags</label>
                    <CreatableReactSelect
                        isMulti 
                        value={selectedTags.map((tag)=>{
                            return {label:tag.label, value:tag.id}})}
                        onChange={tags=>{
                            console.log(tags, "tag", selectedTags, "select"); 
                            setSelectedTags(tags.map((tag)=>{return {label:tag.label, id:tag.value}}))
                            console.log(tags, "tag", selectedTags, "select"); 
                            }} />
                    <div className="container_body">
                        <input ref={bodyRef} type="textarea" />
                     </div>
                    <div className="container_buttons">
                        <button>Cancel</button>
                        <input type={"submit"}  />
                     </div>
                </form>
            </div>
        </div>
    )
}

export default NoteForm;
