import React, { useMemo } from 'react';
import { Navigate, Route, Routes } from 'react-router-dom';
import './App.css';
import Newnote from './Newnote';
import { UseLocalStorage } from './UseLocalStorage';
import { v4 as uuidv4 } from 'uuid';
import NoteList from './NoteList';

function App() {
  const [notes, setNotes] = UseLocalStorage("NOTES", []);
  const [tags, setTags] = UseLocalStorage("TAGS", []);

  const NoteWithTags = useMemo(()=>{
    notes.map((note)=>{
      return{...note, tag:tags.filter((tag)=>{
        return note.tagIds.includes(tag.id)
      }) }
    })
  },[notes, tags])

  const onCreateNote = ({tags, ...data})=>{
    console.log('truely verily')
    setNotes((prev)=>[...prev, {...data, id: uuidv4(), tagIds: tags.map((tag)=> {return tag.id})}])
  }

  const onAddTag = (tag)=>{
    setTags(prev=>[...prev, tag])
  }

  return (
    <div className="App">
      <Routes>
        <Route path='/' element={<NoteList NoteWithTags={NoteWithTags} />}/>
        <Route path='/new' element={<Newnote onCreateNote={onCreateNote} onAddTag={onAddTag} tags={tags} />}/>
        <Route path='*' element={<Navigate to={'/'} />}/>
        <Route path='/:id'>
          <Route index element={<h1>Show</h1>} />
          <Route path='edit' element={<h1>Edit</h1>} />
        </Route>
      </Routes>
    </div>
  );
}

export default App;
