import React from "react";
import { useParams } from "react-router-dom";

const Note = ({notes})=>{

    const {id} = useParams()
    const note = notes.find(n=>n.id===id)


    return(
        <div className="noteContainer">
            <div style={{"display":"flex", "flexDirection":"row", "justifyContent":"space-between"}} className="noteHeader">
                <h1>Note</h1>
                <button onClick={()=>console.log(notes,"theNotess")}>params</button>
            </div>
            <div className="noteDetails">
                <div className="noteTitleandTags">
                    <input type="text" defaultValue={note.title} />
                    <div className="noteTags">
                        {note.tag.map(t=>{
                           return <div key={t.id} className="tag">{t.label}</div>
                        })}
                    </div>

                </div>
                <div className="noteBody"></div>
            </div>
        </div>
    )
}

export default Note;
