import React from 'react';

const Form = ({todo, setTodo}) => {
    const handleInputTaskChange = (event) => {
        event.preventDefault()
        setTodo(event.target.value)
    }

  return (
    <form>
        <input type="text" className="taskinput" placeholder='Enter Todo...' value={todo} onChange={(event) => handleInputTaskChange(event)}/>
        <button className='addbutton' type='submit'>Add</button>
    </form>
  )
}

export default Form
