import React from "react";
import "./DayCard.css"

const DayCard = ({data, unit}) => {

    const {temp, date, desc, icon} = data

    const newDate = new Date(date * 1000)
    const day = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
    const month = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"]

    return(
        <div className="card">
            <h3>{day[newDate.getDay()]}</h3>
            <p>{`${month[newDate.getMonth()]} ${newDate.getDate()}`}<sub>th</sub> </p>
            <img src= {`http://openweathermap.org/img/wn/${icon}@2x.png`} alt="weatherIcon" />
            <h3>{unit === "imperial" ? `${temp.toFixed(0)}째F`:`${temp.toFixed(0)}째C`}</h3>
            <p>{desc}</p>
        </div>
    )
};

export default DayCard;
