import React, { useEffect, useState } from "react";
import './ForecastContainer.css'; 
import DayCard from './DayCard';
import getWeatherData from "../services";


const ForecastContainer = () =>{

    const [unit, setUnit] = useState("imperial")
    const [city, setCity] = useState("");

    const updateUnit = ({target:value}) =>{
        setUnit(value)
    };

    useEffect(()=>{
        const weatherData = async()=>{
            const weather = await getWeatherData(city, unit)
        }
    })

    return(
        <div className="container">
            <div className="header">
                <h1> 5 Day Forecast</h1>
            </div>
            <div className="container__details">
                <div className="details__city">

                    <h5>Austin TX, US</h5>

                    <div className="details__units">
                        <div>
                            <label htmlFor="fahrenheit">Fahrenheit</label>
                            <input type="radio" id="fahrenheit" name="tempUnit" value="imperial" onChange={updateUnit}/>
                        </div>
                        <div>
                            <label htmlFor="celsius">Celsius</label>
                            <input type="radio" id="celsius" name="tempUnit" value="metric" onChange={updateUnit}/>
                        </div>
                    </div>
                </div>
            </div>
            <div className="cards">
                <DayCard/>
            </div>
        </div>
    )
};

export default ForecastContainer;
