const API_KEY = process.env.REACT_APP_API_KEY;
// const url = "api.openweathermap.org/data/2.5/forecast?";

const getWeatherData = async (city, unit="metric") =>{
    const URL = `api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${API_KEY}&units=${unit}`;
    const res = await fetch(URL);
    if (res.ok){
        const response = res.json()
        const data = response.list.filter((item)=>item.dt_txt.includes("00:00:00")).map((item)=>
        ({
            temp : item.main.temp,
            date : item.dt,
            desc : item.weather[0].description,
            icon : item.weather[0].id

        }
        )

        )
        return data;
    }
}

export default getWeatherData;
