import React, { useEffect, useState } from "react";
import './ForecastContainer.css'; 
import DayCard from './DayCard';
import getWeatherData from "../services";



const ForecastContainer = () =>{

    const [weatherData, setWeatherData] = useState(null);
    const [unit, setUnit] = useState("imperial");
    const [city, setCity] = useState("q=paris");
    const [isLoading, setIsLoading] = useState(false)
    const [error, setError] = useState([])
    const [currentLocation, setCurrentLocation] = useState("")

    const HandleKeyPress = async(e) =>{
        if(e.key==="Enter"){
            setCity(e.target.value);
            e.target.value=""
            e.target.blur();
        }
    };

    useEffect(()=>{
        console.log("running1")
        const getCurrentLocation = ()=>{
            console.log("running2")

            const getCoordinate = (position)=>{
                const location = `lat=${position.coords.latitude}&lon=${position.coords.longitude}`
                console.log(location)
                setCurrentLocation(location)
            }

            console.log("running3")
            if(navigator.geolocation){
                console.log("running4")
                // try{
                    navigator.geolocation.getCurrentPosition(getCoordinate)
                // }catch(err){setError([...error, err.message])}
            }else{setError([...error, "Geolocation Unsupported"])}

        }
    }, [])

    // useEffect(()=>{
    //     const fetchWeatherData = async()=>{
    //         console.log("running3")
    //         setIsLoading(true)
    //         const data = await getWeatherData(city || currentLocation, unit)
    //         setIsLoading(false)
    //         setWeatherData(data)
    //         setError([])
    //     }
    //     fetchWeatherData()
    //     .catch((err)=>{
    //         console.log(err)
    //         setIsLoading(false);
    //         setError([...error, err.message]);
    //     })
    // }, [])

    return(
        <div className="container">
            <div className="header">
                <h1> Five (5) Day Forecast</h1>
                <h2>{city.toUpperCase()}</h2>
            </div>
            <div className="container__details">
                <div className="details__city">

                    <input type="text" placeholder="Enter city..." onKeyDown={HandleKeyPress} />

                    <div className="details__units">
                        <div>
                            <label htmlFor="fahrenheit">Fahrenheit</label>
                            <input
                                type="radio" 
                                id="fahrenheit" 
                                name="tempUnit" 
                                checked = {unit === "imperial"}
                                value="imperial" 
                                onChange={({target: {value}}) =>{setUnit(value) }}/>
                        </div>
                        <div>
                            <label htmlFor="celsius">Celsius</label>
                            <input
                            type="radio" 
                            id="celsius" 
                            name="tempUnit" 
                            checked = {unit === "metric"}
                            value="metric" 
                            onChange={({target: {value}}) =>{setUnit(value) }}/>
                        </div>
                    </div>
                </div>
            </div>
            <div className="cards">
             {isLoading? "loading data...": weatherData?.map((item)=>(
                 <DayCard key={item.date} unit={unit} data={item}/>
                 )
             )}
                {(!weatherData && !isLoading)? <h4>Please Enter City...</h4>:""}
            </div>
            <div>
              {error?.map((message)=>(<h3 style={{color: "red"}}>Error loading data.. `${message}` </h3>))}
            </div>
        </div>
    )
};

export default ForecastContainer;
