import React, { useEffect, useState } from "react";
import './ForecastContainer.css'; 
import DayCard from './DayCard';
import getWeatherData from "../services";


const ForecastContainer = () =>{

    const [weatherData, setWeatherData] = useState(null);
    const [unit, setUnit] = useState("imperial");
    const [city, setCity] = useState("paris");

    const HandleKeyPress = (e) =>{
        // e.preventDefault();
        console.log("pressed")
        if(e.target.key==="Enter")setCity(e.target.value);
        // e.target.blur();
    };

    useEffect(()=>{
        const fetchWeatherData = async()=>{
            const data = await getWeatherData(city, unit)
            setWeatherData(data)
        }
        fetchWeatherData();
        console.log(city,"test");
        console.log(weatherData,"test1");
    }, [city, unit])

    return(
        <div className="container">
            <div className="header">
                <h1> 5 Day Forecast</h1>
            </div>
            <div className="container__details">
                <div className="details__city">

                    <input type="text" placeholder="Enter city..." onKeyDown={HandleKeyPress} />

                    <div className="details__units">
                        <div>
                            <label htmlFor="fahrenheit">Fahrenheit</label>
                            <input
                                type="radio" 
                                id="fahrenheit" 
                                name="tempUnit" 
                                value="imperial" 
                                onChange={({target: {value}}) =>{setUnit(value) }}/>
                        </div>
                        <div>
                            <label htmlFor="celsius">Celsius</label>
                            <input
                            type="radio" 
                            id="celsius" 
                            name="tempUnit" 
                            value="metric" 
                            onChange={({target: {value}}) =>{setUnit(value) }}/>
                        </div>
                    </div>
                </div>
            </div>
            <div className="cards">
                <DayCard/>
            </div>
        </div>
    )
};

export default ForecastContainer;
