import React from "react";
import "./DayCard.css"
import rain from "../assets/rainy.jpg";

const DayCard = ({data, unit}) => {

    const {temp, date, desc, icon} = data

    const newDate = new Date((date * 1000));
    const days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
    const day = newDate.getDate();
    const month = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];

    return(
        <div className="card">
            <div style={{background:  rgba(0, 0, 0, 0.3) `url(${rain})`, opacity:0.8 }} className="overlay">
                <h3>{days[newDate.getDay()]}</h3>
                <p>{`${month[newDate.getMonth()]} ${day}`}
                    <sup>{day.toString().endsWith(2) ? "nd"
                    :day.toString().endsWith(3) ? "rd"
                    :day.toString().endsWith(1) ? "st"
                    :day === 11? "th"
                    :day === 12? "th"
                    :day === 13? "th" : "th" }
                    </sup> 
                </p>
                <img src= {`http://openweathermap.org/img/wn/${icon}@2x.png`} alt="weatherIcon" />
                <h3>{unit === "imperial" ? `${temp.toFixed(0)}째F`:`${temp.toFixed(0)}째C`}</h3>
                <p>{desc}</p>
            </div>
        </div>
    )
};

export default DayCard;
